sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (current.location == previous.location) {
        return;
    }

    //Set location ref field to be that of the sat img rec
    // var locRef = current.assignment.satellite.location_reference;
    // current.location_name = locRef;

    var gp = new GlideGeoPoint();
    gp.setValue(current.location);
    var longitude = gp.getLongitude();
    var latitude = gp.getLatitude();
    var city = current.number;
    var name = city + " Location";

    //See if location already exists in table
    var gl = new GlideRecord('cmn_location');
    gl.addQuery('name', name);
    gl.query();

    if (gl.next()) {
		//Update existing location record
		gl.longitude = longitude;
		gl.latitude = latitude;
		var id = gl.update();
		current.location_name = id;
    } else {
        //Create new location record
        var gr = new GlideRecord('cmn_location');
        gr.initialize();
        gr.setValue('name', name);
        gr.setValue('city', city);
        gr.setValue('latitude', latitude);
        gr.setValue('longitude', longitude);
        var localID = gr.insert();
        current.location_name = localID;
    }

})(current, previous);
