sys_script_include.script:
var createSatelliteTask = Class.create();
createSatelliteTask.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	newTask: function(){

		var assignment = this.getParameter("assignedAgent");
		var grDist = new GlideRecord("x_1387186_ows_distance");
		grDist.addQuery("sys_id",assignment);
		grDist.query();
		grDist.next();

		var gr = new GlideRecord("x_1387186_ows_satellite_task");
		gr.initialize();
		gr.assignment = assignment;
		gr.location = grDist.satellite.location.getDisplayValue();
		if(grDist.table=="agent"){
			gr.assigned_to = grDist.asset_agent;
		}
		else{
			var grAsset = new GlideRecord("x_1387186_intellat_assets");
			grAsset.addQuery(sys_id,grDist.asset_agent);
			grAsset.query();
			grAsset.next();
			gr.assigned_to = grAsset.managing_agent;
		}

		var sysID = gr.insert();

		return; //JSON.stringify({"redirect":"sysID"});

	},
    type: 'createSatelliteTask'
});
